// 4.min.js
// 목표 : 최소값을 찾는다!
function getMin(a, b) {
    if (a > b) {
        return b
    }
    return a
}

// 또는

function getMin2(a, b) {
    var min;
    if (a > b) {
        min = b
    } else {
        min = a
    }
    return min
}

var min = getMin(3, 4)
console.log(min)
var min = getMin2(3, 4)
console.log(min)


function getMinFromArray(numbers) {
    var min = Infinity  // 어떠한 값보다 큰 값
    // TODO: 배열에서 최소값을 구하여 min에 할당
    for (num of numbers) {
        // 삼항 연산자
        min = min > num ? num : min
        // if (min > num) {
        //     min = num
        // }   
    }
    return min
}
var numbers = [1, 2, 3, 4, 5]
var min = getMinFromArray(numbers) // 1
console.log(min)
